import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditUser = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [gender, setGender] = useState('Male');
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => { 
        getUserById();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    const getUserById = async () => {
        const response = await axios.get(`http://localhost:3000/mahasiswa/${id}`);
        setName(response.data.name);
        setEmail(response.data.email);
        setGender(response.data.gender);
    };

    const updateUser = async (e) => {
        e.preventDefault();
        try {
            await axios.patch(`http://localhost:3000/mahasiswa/${id}`, {
                name,
                email,
                gender
            });
            navigate('/');
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div className='columns'>
            <div className='column is-4 is-offset-4'>
                <form onSubmit={updateUser}>
                    <div className='field'>
                        <label className='label'>Name</label>
                        <div className='control'>
                            <input className='input' value={name} onChange={ (e)=> setName(e.target.value)} type='text' placeholder='Name' />
                        </div>
                    </div>

                    <div className='field'>
                        <label className='label'>Email</label>
                        <div className='control'>
                            <input className='input' type='text' value={email} onChange={ (e)=> setEmail(e.target.value)} placeholder='Email' />
                        </div>
                    </div>
                    
                    <div className='field'>
                        <label className='label'>Gender</label>
                        <div className='control'>
                            <div className='select is-fullwidth'>
                                <select value={gender} onChange={ (e)=> setGender(e.target.value)}>
                                    <option value="Male">Male</option>
                                    <option value="Female">Female</option>
                                </select>
                            </div>
                        </div>
                    </div>

                    <div className='field'>
                        <div className='control'>
                            <button type="submit" className='button is-success'>Update</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default EditUser;
